package com.licitacija.mk.controllers.rest;import com.licitacija.mk.models.City;import com.licitacija.mk.service.CityService;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping(value = "/city",produces = MediaType.APPLICATION_JSON_VALUE)public class CityController {    private final CityService service;    public CityController(CityService service) {        this.service = service;    }    @GetMapping("/all")    public List<City> findAllCities(){        return service.findAllCities();    }}