package com.licitacija.mk.controllers.rest;import com.licitacija.mk.models.Interest;import com.licitacija.mk.service.InterestService;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.Map;@RestController@CrossOrigin("*")@RequestMapping(value = "/interest", produces = MediaType.APPLICATION_JSON_VALUE)public class InterestController {    private final InterestService service;    public InterestController(InterestService service) {        this.service = service;    }    @GetMapping("/byProduct/{productId}")    @ResponseStatus(HttpStatus.OK)    public List<Interest> getInterestByProduct(@PathVariable("productId") long productId) {        return service.getInterestByProduct(productId);    }    @GetMapping("/byUser/{userId}")    @ResponseStatus(HttpStatus.OK)    public List<Interest> getInterestByUser(@PathVariable("userId") long userId) {        return service.getInterestByUser(userId);    }    @PostMapping    @ResponseStatus(HttpStatus.CREATED)    public void createInterest(@RequestBody Map<String, String> payload) {        service.createInterest(payload.get("userId"), payload.get("productId"));    }    @DeleteMapping    @ResponseStatus()    public void deleteInterest(@PathVariable("interestId") long interestId) {        service.deleteInterest(interestId);    }}