package com.licitacija.mk.controllers.rest;import com.licitacija.mk.models.Offer;import com.licitacija.mk.service.OfferService;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.*;import javax.servlet.http.HttpServletResponse;import java.util.List;import java.util.Map;@RestController@RequestMapping(value = "/offer",produces = MediaType.APPLICATION_JSON_VALUE)public class OfferController {    private final OfferService service;    public OfferController(OfferService service) {        this.service = service;    }    @PostMapping    @ResponseStatus(HttpStatus.CREATED)    public void addNewOffer(@RequestBody Map<String,String> payload, HttpServletResponse response) {        service.addNewOffer(payload.get("userId"),payload.get("productId"),payload.get("price"));    }    @GetMapping("/forProduct/{productId}")    @ResponseStatus(HttpStatus.OK)    public List<Offer> findAllOffersForProduct(@PathVariable("productId") String productId) {        return service.findAllOffersForProduct(productId);    }    @GetMapping("/forUser/{userId}")    @ResponseStatus(HttpStatus.OK)    public List<Offer> findAllOffersFromUser(@PathVariable("userId") String userId) {        return service.findAllOffersFromUser(userId);    }    @GetMapping("/latestByUser/{userId}")    @ResponseStatus(HttpStatus.OK)    public Offer findLatestOfferByUser(@PathVariable("userId") String userId){        return service.findLatestOfferByUser(userId);    }}