package com.licitacija.mk.models;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;@Entitypublic class Interest {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private Long interest_id;    @ManyToOne    @JoinColumn(name = "user_id")    @JsonIgnore    User user;    @ManyToOne    @JoinColumn(name = "product_id")    @JsonIgnore    Product product;    public Interest(){}    public Interest(User user, Product product) {        this.user = user;        this.product = product;    }    public Long getInterest_id() {        return interest_id;    }    public void setInterest_id(Long interest_id) {        this.interest_id = interest_id;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Product getProduct() {        return product;    }    public void setProduct(Product product) {        this.product = product;    }}