package com.licitacija.mk.models;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;@Entitypublic class Interest {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private Long interestId;    @ManyToOne    @JoinColumn(name = "user_id")    @JsonIgnore    private User user;    @ManyToOne    @JoinColumn(name = "product_id")    @JsonIgnore    private Product product;    public Interest(){}    public Interest(User user, Product product) {        this.user = user;        this.product = product;    }    public Long getInterestId() {        return interestId;    }    public void setInterestId(Long interestId) {        this.interestId = interestId;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Product getProduct() {        return product;    }    public void setProduct(Product product) {        this.product = product;    }}