package com.licitacija.mk.models;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.time.LocalDateTime;@Entitypublic class Offer {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private long offerId;    private long price;    private LocalDateTime date;    @ManyToOne    @JoinColumn(name = "user_id")    @JsonIgnore    private User givenByUser;    @NotNull    @ManyToOne    @JoinColumn(name = "product_id")    @JsonIgnore    private Product givenForProduct;    public Offer(){}    public Offer(long price, LocalDateTime date, User givenByUser, @NotNull Product givenForProduct) {        this.price = price;        this.date = date;        this.givenByUser = givenByUser;        this.givenForProduct = givenForProduct;    }    public long getOfferId() {        return offerId;    }    public void setOfferId(long offerId) {        this.offerId = offerId;    }    public long getPrice() {        return price;    }    public void setPrice(long price) {        this.price = price;    }    public LocalDateTime getDate() {        return date;    }    public void setDate(LocalDateTime date) {        this.date = date;    }    public User getGivenByUser() {        return givenByUser;    }    public void setGivenByUser(User givenByUser) {        this.givenByUser = givenByUser;    }    public Product getGivenForProduct() {        return givenForProduct;    }    public void setGivenForProduct(Product givenForProduct) {        this.givenForProduct = givenForProduct;    }}