package com.licitacija.mk.models;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.time.LocalDateTime;@Entitypublic class Offer {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private long offer_id;    private long price;    private LocalDateTime date;    @ManyToOne    @JoinColumn(name = "user_id")    private User givenByUser;    @NotNull    @ManyToOne()    @JoinColumn(name = "product_id")    private Product givenForProduct;    public Offer(){}    public Offer(long price, LocalDateTime date, User givenByUser, @NotNull Product givenForProduct) {        this.price = price;        this.date = date;        this.givenByUser = givenByUser;        this.givenForProduct = givenForProduct;    }    public long getOffer_id() {        return offer_id;    }    public void setOffer_id(long offer_id) {        this.offer_id = offer_id;    }    public long getPrice() {        return price;    }    public void setPrice(long price) {        this.price = price;    }    public LocalDateTime getDate() {        return date;    }    public void setDate(LocalDateTime date) {        this.date = date;    }    public User getGivenByUser() {        return givenByUser;    }    public void setGivenByUser(User givenByUser) {        this.givenByUser = givenByUser;    }    public Product getGivenForProduct() {        return givenForProduct;    }    public void setGivenForProduct(Product givenForProduct) {        this.givenForProduct = givenForProduct;    }}