package com.licitacija.mk.models;import javax.persistence.*;import javax.validation.constraints.NotNull;@Entitypublic class Picture {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private long picture_id;    @Lob    private byte[] image;    @NotNull    @ManyToOne()    @JoinColumn(name = "product_id")    private Product product;    public Picture() {}    public Picture(byte[] image, @NotNull Product product) {        this.image = image;        this.product = product;    }    public long getPicture_id() {        return picture_id;    }    public byte[] getImage() {        return image;    }    public void setImage(byte[] image) {        this.image = image;    }    public Product getProduct() {        return product;    }    public void setProduct(Product product) {        this.product = product;    }}