package com.licitacija.mk.models;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.ArrayList;import java.util.List;@Entity@Table(name = "Product")public class Product {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private long product_id;    private long start_price;    private long max_price;    private String title;    private String description;    private LocalDateTime datePublished;    private LocalDateTime startAuction;    private LocalDateTime endAuction;    @ManyToOne    @JoinColumn(name = "city_id")    private City city;    @OneToMany(mappedBy = "product")    private List<Picture> productsPictures;    @ManyToOne()    @JoinColumn(name = "user_id")    @JsonIgnore    private User user;    @OneToMany(mappedBy = "givenForProduct", cascade = CascadeType.REMOVE)    @JsonIgnore    private List<Offer> offers;    @OneToMany(mappedBy = "product")    @JsonIgnore    private List<Interest> interests;    public Product(){        this.datePublished=LocalDateTime.now();    }    public Product(long start_price, String title, String description, LocalDateTime datePublished, User user, City city) {        this.start_price = start_price;        this.title = title;        this.description = description;        this.datePublished = LocalDateTime.now();        this.user = user;        this.city = city;        this.max_price = this.start_price;    }    public long getMax_price() {        return max_price;    }    public void setMax_price(long max_price) {        this.max_price = max_price;    }    public long getStart_price() {        return start_price;    }    public void setStart_price(long start_price) {        this.start_price = start_price;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public LocalDateTime getDatePublished() {        return datePublished;    }    public void setDatePublished(LocalDateTime datePublished) {        this.datePublished = datePublished;    }    public LocalDateTime getStartAuction() {        return startAuction;    }    public void setStartAuction(LocalDateTime startAuction) {       this.startAuction=startAuction;    }    public LocalDateTime getEndAuction() {        return endAuction;    }    public void setEndAuction(LocalDateTime endAuction) {        this.endAuction=endAuction;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public List<Offer> getOffers() {        if (this.offers == null) {            this.offers = new ArrayList<>();        }        return offers;    }    public void setOffers(List<Offer> offers) {        this.offers = offers;    }    public long getProduct_id() {        return product_id;    }    public void setProduct_id(int product_id) {        this.product_id = product_id;    }    public List<Picture> getProductsPictures() {        if (this.productsPictures == null) {            this.productsPictures = new ArrayList<>();        }        return productsPictures;    }    public City getCity() {        return city;    }    public void setCity(City city) {        this.city = city;    }    public void setProductsPictures(List<Picture> productsPictures) {        this.productsPictures = productsPictures;    }}