package com.licitacija.mk.models;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;import java.time.LocalDateTime;import java.util.List;@Entity@Table(name = "Product")public class Product {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private long productId;    private long startPrice;    private long maxPrice;    private String title;    private String description;    private LocalDateTime datePublished;    private LocalDateTime startAuction;    private LocalDateTime endAuction;    @ManyToOne    @JoinColumn(name = "city_id")    private City city;    @OneToMany(mappedBy = "product")    private List<Picture> productsPictures;    @ManyToOne()    @JoinColumn(name = "user_id")    @JsonIgnore    private User user;    @OneToMany(mappedBy = "givenForProduct", cascade = CascadeType.REMOVE, orphanRemoval = true)    @JsonIgnore    private List<Offer> offers;    @OneToMany(mappedBy = "product", cascade = CascadeType.REMOVE, orphanRemoval = true)    @JsonIgnore    private List<Interest> interests;    public Product() {        this.datePublished = LocalDateTime.now();    }    public Product(long startPrice, String title, String description, LocalDateTime datePublished, User user, City city) {        this.startPrice = startPrice;        this.title = title;        this.description = description;        this.datePublished = LocalDateTime.now();        this.user = user;        this.city = city;        this.maxPrice = this.startPrice;    }    public long getMaxPrice() {        return maxPrice;    }    public void setMaxPrice(long maxPrice) {        this.maxPrice = maxPrice;    }    public long getStartPrice() {        return startPrice;    }    public void setStartPrice(long startPrice) {        this.startPrice = startPrice;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public LocalDateTime getDatePublished() {        return datePublished;    }    public void setDatePublished(LocalDateTime datePublished) {        this.datePublished = datePublished;    }    public LocalDateTime getStartAuction() {        return startAuction;    }    public void setStartAuction(LocalDateTime startAuction) {        this.startAuction = startAuction;    }    public LocalDateTime getEndAuction() {        return endAuction;    }    public void setEndAuction(LocalDateTime endAuction) {        this.endAuction = endAuction;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public List<Offer> getOffers() {        return offers;    }    public void setOffers(List<Offer> offers) {        this.offers = offers;    }    public long getProductId() {        return productId;    }    public void setProductId(int productId) {        this.productId = productId;    }    public List<Picture> getProductsPictures() {        return productsPictures;    }    public City getCity() {        return city;    }    public void setCity(City city) {        this.city = city;    }    public void setProductsPictures(List<Picture> productsPictures) {        this.productsPictures = productsPictures;    }}