package com.licitacija.mk.mongoModels;import org.springframework.data.mongodb.core.mapping.Document;import javax.persistence.Id;@Document()public class Offer {    @Id    private String id;    private int price;    public void setId(String id) {        this.id = id;    }    public Offer(int price) {        this.price = price;    }    public String getId() {        return id;    }    public int getPrice() {        return price;    }    public void setPrice(int price) {        this.price = price;    }}