package com.licitacija.mk.repository;import com.licitacija.mk.models.Interest;import com.licitacija.mk.models.Product;import com.licitacija.mk.models.User;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface InterestRepository extends JpaRepository<Interest, Long> {    public List<Interest> findAllByUser(User user);    public List<Interest> findAllByProduct(Product product);}