package com.licitacija.mk.repository;import com.licitacija.mk.models.Offer;import com.licitacija.mk.models.Product;import com.licitacija.mk.models.User;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface OfferRepository extends JpaRepository<Offer, Long> {    List<Offer> findAllByGivenForProduct(Product product);    List<Offer> findAllByGivenByUser(User user);}