package com.licitacija.mk.repository;import com.licitacija.mk.models.City;import com.licitacija.mk.models.Product;import com.licitacija.mk.models.User;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface ProductRepository extends JpaRepository<Product, Long> {    List<Product> findAllByCity(City city);    List<Product> findAllByUser(User user);}