package com.licitacija.mk.service;import com.licitacija.mk.models.City;import com.licitacija.mk.models.expections.NotFoundException;import com.licitacija.mk.repository.CityRepository;import org.springframework.stereotype.Service;@Servicepublic class CityService {    private final CityRepository repository;    public CityService(CityRepository repository) {        this.repository = repository;    }    public City findCityById(long city_id){        return repository.findById(city_id).orElseThrow(()-> new NotFoundException("There is no city with id "+ city_id));    }}